display_title "Git installation check"
GIT_VERSION=`git --version 2>/dev/null`
if [[ -z $GIT_VERSION ]]; then
    echo "Git not detected.";
    if [[ -z $NO_PROMPT ]]; then
      read -p "Install Git ? (y/n) " -n 1 -r
      echo
    else
      REPLY=y
    fi

    if [[ $REPLY =~ ^[Yy]$ ]]
    then
        echo "Install Git (root rights require)..."
        sudo sudo apt-get -y --allow-downgrades --allow-remove-essential --allow-unauthenticated install git
    else
        echo "exit."
        exit 0
    fi
else
  echo "Git installation ok."
fi

